import Aside from "@/components/Aside";
import Header from "@/components/Header";
export default {
  name: 'HomeView',
  data: function data() {
    return {
      tableData: [],
      total: 0,
      pageNum: 1,
      pageSize: 2,
      username: '',
      email: '',
      address: '',
      dialogFormVisible: false,
      multipleSelection: [],
      form: {},
      collapseBtnClass: 'el-icon-s-fold',
      isCollapse: false,
      sideWidth: 200,
      logoTextShow: true
    };
  },
  created: function created() {
    // 请求分页查询数据
    this.load();
  },
  components: {
    Aside: Aside,
    Header: Header
  },
  methods: {
    collapse: function collapse() {
      //点击收缩按钮触发
      this.isCollapse = !this.isCollapse;

      if (this.isCollapse) {
        //收缩
        this.sideWidth = 64;
        this.collapseBtnClass = 'el-icon-s-unfold';
        this.logoTextShow = false;
      } else {
        //展开
        this.sideWidth = 200;
        this.collapseBtnClass = 'el-icon-s-fold';
        this.logoTextShow = true;
      }
    },
    load: function load() {
      var _this = this;

      this.request.get("/user/page", {
        params: {
          pageNum: this.pageNum,
          pageSize: this.pageSize,
          username: this.username,
          email: this.email,
          address: this.address
        }
      }).then(function (res) {
        console.log(res);
        _this.tableData = res.records;
        _this.total = res.total;
      });
    },
    save: function save() {
      var _this2 = this;

      this.request.post("/user", this.form).then(function (res) {
        if (res) {
          _this2.$message.success('保存成功');

          _this2.dialogFormVisible = false;

          _this2.load();
        } else {
          _this2.$message.error('保存失败');
        }
      });
    },
    handleAdd: function handleAdd() {
      this.dialogFormVisible = true;
      this.form = {};
    },
    handleEdit: function handleEdit(row) {
      this.form = Object.assign({}, row);
      this.dialogFormVisible = true;
    },
    del: function del(id) {
      var _this3 = this;

      this.request.delete("/user/" + id).then(function (res) {
        if (res) {
          _this3.$message.success('删除成功');

          _this3.load();
        } else {
          _this3.$message.error('删除失败');
        }
      });
    },
    delBatch: function delBatch() {
      var _this4 = this;

      var ids = this.multipleSelection.map(function (v) {
        return v.id;
      }); // [{}, {}, {}] => [1, 2, 3]

      this.request.post("/user/del/batch", ids).then(function (res) {
        if (res) {
          _this4.$message.success('批量删除成功');

          _this4.load();
        } else {
          _this4.$message.error('批量删除失败');
        }
      });
    },
    handleSelectionChange: function handleSelectionChange(val) {
      console.log(val);
      this.multipleSelection = val;
    },
    reset: function reset() {
      this.username = '';
      this.email = '';
      this.address = '';
      this.load();
    },
    handleSizeChange: function handleSizeChange(pageSize) {
      console.log(pageSize);
      this.pageSize = pageSize;
      this.load();
    },
    handleCurrentChange: function handleCurrentChange(pageNum) {
      console.log(pageNum);
      this.pageNum = pageNum;
      this.load();
    }
  }
};
