import Aside from "@/components/Aside";
import Header from "@/components/Header";
export default {
  name: 'HomeView',

  data() {
    return {
      collapseBtnClass: 'el-icon-s-fold',
      isCollapse: false,
      sideWidth: 200,
      logoTextShow: true,
      user: {}
    };
  },

  created() {
    //一开始从后台获取最新的user数据
    this.getUser();
  },

  components: {
    Aside,
    Header
  },
  methods: {
    collapse() {
      //点击收缩按钮触发
      this.isCollapse = !this.isCollapse;

      if (this.isCollapse) {
        //收缩
        this.sideWidth = 64;
        this.collapseBtnClass = 'el-icon-s-unfold';
        this.logoTextShow = false;
      } else {
        //展开
        this.sideWidth = 200;
        this.collapseBtnClass = 'el-icon-s-fold';
        this.logoTextShow = true;
      }
    },

    getUser() {
      let username = localStorage.getItem("user") ? JSON.parse(localStorage.getItem("user")).username : ''; //从后台获取user数据

      this.request.get('/user/username/' + username).then(res => {
        // 重新赋值后台最新的user数据
        this.user = res.data;
      });
    }

  }
};
